//在结束时加上：

parent.$mc_ctrler.setBishaUP();
parent.$mc_ctrler.setBisha();
parent.$mc_ctrler.setZhao1("招1_QK");
//在结束时加上：parent.$mc_ctrler.endAct();

if(parent.$fighter_ctrler.target){
	parent.$fighter_ctrler.target.visible = false;
}

var bsmc:* = parent.$mc_ctrler.getAttacker("bsmc");
if(bsmc){
	bsmc.gotoAndPlay("fly");
}

parent.$mc_ctrler.endAct();

var bsmc2:* = parent.$mc_ctrler.getAttacker("bsmc");

if(bsmc2){
	bsmc2.gotoAndPlay("miss");
}

//执行移动
parent.$mc_ctrler.move(0,-2); //参数为X,Y
parent.$mc_ctrler.damping(0,0.2); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.move(0,-2); //参数为X,Y
parent.$mc_ctrler.damping(0,0.05); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.movePercent(1,0); //参数为X,Y
parent.$mc_ctrler.dampingPercent(0.1,0); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.movePercent(1.5,0); //参数为X,Y
parent.$mc_ctrler.dampingPercent(0.15,0); //移动阻尼，参数为X,Y

//慢放效果[参数：时间（秒）]
parent.$effect_ctrler.slowDown(0.5);
parent.$effect_ctrler.shine(0xFF9900);

//加入攻击对象[参数：MC名称，params]
/*
params 参数 {
	x:Number(速度X)|{moveToTarget:Boolean(是否移动到目标),followTarget:Boolean(是否跟随目标),offset:Number(距离目标偏移量),range:Array[left,right](攻击范围)} , 
	y:Number(速度Y)|{moveToTarget:Boolean(是否移动到目标),followTarget:Boolean(是否跟随目标),offset:Number(距离目标偏移量),range:Array[top,bottom](攻击范围)} , 
	applyG:Boolean(是否受重力影响) ,
}
*/
parent.$mc_ctrler.addAttacker("zh3mc",{x:{followTarget:true,offset:0}});


//如果比较复杂，需要在MC内部加入代码，实现接口
