//在结束时加上：parent.$mc_ctrler.endAct();

//执行移动
parent.$mc_ctrler.move(-1,-3); //参数为X,Y
parent.$mc_ctrler.damping(0.2,0.2); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.move(-1,-5); //参数为X,Y
parent.$mc_ctrler.damping(0.5,0.5); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.movePercent(0.7,0); //参数为X,Y
parent.$mc_ctrler.dampingPercent(0.4,0); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.movePercent(1.5,0); //参数为X,Y
parent.$mc_ctrler.dampingPercent(0.1,0); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.movePercent(1,0); //参数为X,Y
parent.$mc_ctrler.dampingPercent(0.1,0); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.move(5,-13); //参数为X,Y
parent.$mc_ctrler.damping(0.3,0.5); //移动阻尼，参数为X,Y

//执行移动
parent.$mc_ctrler.move(-15,0); //参数为X,Y
parent.$mc_ctrler.damping(1,0); //移动阻尼，参数为X,Y

//移动一次
parent.$fighter_ctrler.moveOnce(-15,0);

//执行移动
parent.$mc_ctrler.move(-1,0); //参数为X,Y
parent.$mc_ctrler.damping(0.02,0); //移动阻尼，参数为X,Y

//移动一次
//parent.$fighter_ctrler.moveOnce(-10,0);

//发射[参数：MC名称，param]
/*
params 参数 {
	x:Number(速度X)|{moveToTarget:Boolean(是否移动到目标),followTarget:Boolean(是否跟随目标),offset:Number(距离目标偏移量)} , 
	y:Number(速度Y)|{moveToTarget:Boolean(是否移动到目标),followTarget:Boolean(是否跟随目标),offset:Number(距离目标偏移量)} , 
	applyG:Boolean(是否受重力影响) ,
}
*/
parent.$mc_ctrler.addAttacker("cbsmc2",{x:{moveToTarget:true,offset:0},applyG:false});

//加入攻击对象[参数：MC名称，params]
/*
params 参数 {
	x:Number(速度X)|{moveToTarget:Boolean(是否移动到目标),followTarget:Boolean(是否跟随目标),offset:Number(距离目标偏移量),range:Array[left,right](攻击范围)} , 
	y:Number(速度Y)|{moveToTarget:Boolean(是否移动到目标),followTarget:Boolean(是否跟随目标),offset:Number(距离目标偏移量),range:Array[top,bottom](攻击范围)} , 
	applyG:Boolean(是否受重力影响) ,
}
*/
